import React from 'react';
import styles from './Dashboard.module.css';
import { FaSearch } from 'react-icons/fa'; // FontAwesome React icon library
import { NotificationIcon } from '../index';

// DateBar component directly embedded with styles
const DateBar = () => {
  return (
    <div className={styles.dateBar}>
      <span className={styles.currentDate}>Friday, November 18, 2022</span>
      <button className={styles.createBookingButton}>Create booking</button>
    </div>
  );
};

// Statistics component to be positioned outside Header
const Statistics = () => {
  return (
    <section className={styles.statistics} aria-label="Hotel Statistics">
      {/* Iterate over stats to display individual stat cards */}
      <div className={styles.statCard}>
        <div className={styles.statLabel}>Today's</div>
        <div className={styles.statType}>Check-in</div>
        <div className={styles.statValue}>23</div>
      </div>
      <div className={styles.statCard}>
        <div className={styles.statLabel}>Today's</div>
        <div className={styles.statType}>Check-out</div>
        <div className={styles.statValue}>13</div>
      </div>
      <div className={styles.statCard}>
        <div className={styles.statLabel}>Total</div>
        <div className={styles.statType}>In hotel</div>
        <div className={styles.statValue}>60</div>
      </div>
      <div className={styles.statCard}>
        <div className={styles.statLabel}>Total</div>
        <div className={styles.statType}>Available room</div>
        <div className={styles.statValue}>10</div>
      </div>
      <div className={styles.statCard}>
        <div className={styles.statLabel}>Total</div>
        <div className={styles.statType}>Occupied room</div>
        <div className={styles.statValue}>90</div>
      </div>
    </section>
  );
};

export const Header = () => {
  return (
    <header className={styles.headerContainer}>
      <div className={styles.header}>
        {/* Embedded SearchBar using existing CSS styles */}
        <div className={styles.searchBarContainer}>
          <FaSearch className={styles.searchIcon} />
          <input
            type="search"
            placeholder="Search for rooms and offers"
            className={styles.searchInput}
            aria-label="Search for rooms and offers"
          />
        </div>

        <div className={styles.headerActions}>
          <button aria-label="Notifications" className={styles.notificationButton}>
            <NotificationIcon />
          </button>
          {/* Add your image */}
          <img src="/.jpg" alt="User profile" className={styles.userAvatar} />
        </div>
      </div>

      {/* DateBar positioned below Header */}
      <DateBar />
      <Statistics/>
    </header>
  );
};
