import React, { useState } from 'react';
import styles from './PieChart.module.css';

const monthlyData = [
  { month: 'May', value: 85 },
  { month: 'Jun', value: 65 },
  { month: 'Jul', value: 80 },
  { month: 'Aug', value: 45 },
  { month: 'Sep', value: 95 },
  { month: 'Oct', value: 85 },
  { month: 'Nov', value: 85 },
  { month: 'Dec', value: 85 },
  { month: 'Jan', value: 95 },
  { month: 'Feb', value: 94 },
];

const percentages = ['100%', '75%', '50%', '25%', '0%'];

export const OccupancyChart = () => {
  const [selectedFilter, setSelectedFilter] = useState('Monthly');

  const handleFilterChange = () => {
    setSelectedFilter(selectedFilter === 'Monthly' ? 'Weekly' : 'Monthly');
  };

  return (
    <div 
      className={styles.container}
      role="region" 
      aria-label="Occupancy Statistics Chart"
    >
      <div className={styles.header}>
        <h2 className={styles.title}>Occupancy Statistics</h2>
        <button 
          className={styles.filterButton}
          onClick={handleFilterChange}
          aria-label={`Switch to ${selectedFilter === 'Monthly' ? 'Weekly' : 'Monthly'} view`}
        >
          <svg 
            width="20" 
            height="20" 
            viewBox="0 0 20 20" 
            fill="none" 
            xmlns="http://www.w3.org/2000/svg"
          >
            <path 
              d="M5 10H15M2.5 5H17.5M7.5 15H12.5" 
              stroke="#667085" 
              strokeWidth="1.67" 
              strokeLinecap="round" 
              strokeLinejoin="round" 
            />
          </svg>
          {selectedFilter}
        </button>
      </div>

      <div className={styles.chartContainer}>
        <div className={styles.yAxis} aria-hidden="true">
          {percentages.map(percentage => (
            <div key={percentage}>{percentage}</div>
          ))}
        </div>

        <div className={styles.chart}>
          <div className={styles.gridLines} aria-hidden="true">
            {percentages.map((_, index) => (
              <div key={index} className={styles.gridLine} />
            ))}
          </div>

          <div 
            className={styles.bars}
            role="group" 
            aria-label={`${selectedFilter} occupancy rates`}
          >
            {monthlyData.map(({ month, value }) => (
              <div 
                key={month} 
                className={styles.barGroup}
                role="group"
                aria-label={`${month}: ${value}% occupancy`}
              >
                <div 
                  className={styles.bar}
                  style={{ height: `${value}%` }}
                />
                <div className={styles.monthLabel}>{month}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
